import React, { useState } from 'react';
import Display from './Display';
import Button from './Button';
import ConfettiExplosion from 'react-confetti-explosion';

const Calculator = () => {
  const [displayValue, setDisplayValue] = useState('0');
  const [confetti, setConfetti] = useState(false);
  const [waitingForOperand, setWaitingForOperand] = useState(false);
  const [operator, setOperator] = useState(null);
  const [value, setValue] = useState(null);

  const handleClick = (label) => {
    if (!isNaN(label)) {
      if (waitingForOperand) {
        setDisplayValue(label);
        setWaitingForOperand(false);
      } else {
        setDisplayValue(displayValue === '0' ? label : displayValue + label);
      }
    } else {
      switch (label) {
        case '+':
        case '-':
        case '×':
        case '÷':
        case '=':
          handleOperation(label);
          break;
        case 'AC':
          setDisplayValue('0');
          setValue(null);
          setOperator(null);
          break;
        default:
          break;
      }
    }
  };

  const handleOperation = (nextOperator) => {
    const inputValue = parseFloat(displayValue);

    if (value == null) {
      setValue(inputValue);
    } else if (operator) {
      const currentValue = value || 0;
      let newValue = currentValue;

      switch (operator) {
        case '+':
          newValue += inputValue;
          break;
        case '-':
          newValue -= inputValue;
          break;
        case '×':
          newValue *= inputValue;
          break;
        case '÷':
          if (inputValue === 0) {
            newValue = 'Error';
          } else {
            newValue /= inputValue;
          }
          break;
        default:
          break;
      }

      setValue(newValue);
      setDisplayValue(String(newValue));
    }

    setWaitingForOperand(true);
    setOperator(nextOperator);

    if ((nextOperator === '+' || nextOperator === '×') && (inputValue === 5 || inputValue === 6)) {
      setConfetti(true);
      setTimeout(() => setConfetti(false), 2000);
    }
  };

  return (
    <div className="calculator">
      {confetti && <ConfettiExplosion />}
      <Display value={displayValue} />
      <div className="button-row">
        {['7', '8', '9', '÷'].map((label) => (
          <Button key={label} label={label} onClick={handleClick} />
        ))}
      </div>
      <div className="button-row">
        {['4', '5', '6', '×'].map((label) => (
          <Button key={label} label={label} onClick={handleClick} />
        ))}
      </div>
      <div className="button-row">
        {['1', '2', '3', '-'].map((label) => (
          <Button key={label} label={label} onClick={handleClick} />
        ))}
      </div>
      <div className="button-row">
        {['0', 'AC', '=', '+'].map((label) => (
          <Button key={label} label={label} onClick={handleClick} />
        ))}
      </div>
    </div>
  );
};

export default Calculator;
